-- ----------------------------
-- Table structure for `book_comments`
-- ----------------------------
DROP TABLE IF EXISTS `book_comments`;
CREATE TABLE `book_comments` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '评论内容 id',
  `url` varchar(255) DEFAULT NULL COMMENT '原文地址',
  `uid` int(11) NOT NULL DEFAULT '0' COMMENT '发表评论的用户id',
  `full_name` varchar(50) DEFAULT NULL COMMENT '评论者昵称',
  `addtime` datetime DEFAULT '2000-01-01 00:00:00' COMMENT '评论时间',
  `content` text COMMENT '评论内容',
  `type` smallint(1) DEFAULT '1' COMMENT '评论类型；1实名评论',
  `status` smallint(1) DEFAULT '1' COMMENT '状态，1已审核，0未审核',
  PRIMARY KEY (`id`),
  KEY `comment_post_ID` (`post_id`) USING BTREE,
  KEY `comment_approved_date_gmt` (`status`) USING BTREE,
  KEY `table_id_status` (`post_id`,`status`) USING BTREE,
  KEY `createtime` (`addtime`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COMMENT='评论表';

