-- ----------------------------
-- Table structure for `book_details`
-- ----------------------------
DROP TABLE IF EXISTS `book_details`;
CREATE TABLE `book_details` (
  `id` mediumint(9) NOT NULL AUTO_INCREMENT,
  `add_id` mediumint(8) DEFAULT NULL COMMENT '添加图书的管理员ID',
  `book_type` varchar(50) DEFAULT NULL COMMENT '图书分类',
  `book_name` varchar(50) DEFAULT NULL COMMENT '书名',
  `book_pic` varchar(200) DEFAULT NULL COMMENT '封面',
  `book_num` smallint(6) DEFAULT '0' COMMENT '库存',
  `book_price` decimal(6,0) DEFAULT NULL COMMENT '价格',
  `book_content` text COMMENT '图书简介',
  `book_pubdate` varchar(20) DEFAULT NULL COMMENT '出版日期',
  `ISBN` varchar(50) DEFAULT NULL COMMENT '图书ISBN码',
  `book_publisher` varchar(50) DEFAULT NULL COMMENT '出版社',
  `book_author` varchar(50) DEFAULT NULL COMMENT '作者',
  `addtime` datetime DEFAULT NULL COMMENT '添加时间',
  `status` tinyint(1) DEFAULT '1' COMMENT '2:已下架 1:已上架',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=195 DEFAULT CHARSET=utf8 COMMENT='图书详情表';

-- ----------------------------
-- Records of book_details
-- ----------------------------
INSERT INTO `book_details` VALUES ('191', null, '1', '好好学习', 'http://open.6api.net/mpic/s29276401.jpg', '0', '42', '? 为什么读了这么多书，依旧过不好这一生？\n因为大多数人都掉进了“低水平勤奋”的陷阱。我们读了很多书，却只是读到了一个个孤立的知识点，而并没有把新旧知识连成一张知识网，长成一棵知识树。\n? 学海真的无涯吗？到底什么样的“知识”值得我们花精力去学？\n知识无穷无尽，但掌控这个世界运行的底层规律是有限的。你需要的是从海量知识中辨别出其中的“王炸”，一通百通！\n学海非无涯，知识有主次。\n? 为什么学习“学习的方法”，比学习本身更重要？\n世界这么大，而我们的智慧有限。只有掌握了更有效的学习方法，才能在极为有限的时间里，把自己的认知水平比别人多往前推进一厘米。\n拥有学习力的人，才拥有这个时代的终极竞争力\n? 如何从“知道”到“做到”？怎么才能让知识变现？\n刻意练习+多场景思维训练=构建底层认知，打通知识的底层结构\n让知识成为资产，让学习成为财富积累的过程！\n被罗辑思维评为十位“全国最会学习的人之一”、“得到”最受欢迎的说书人成甲，为你解开学习迷思，讲授“学习的方法”，帮助你将零碎的知识体系打造为高效的知识管理体系，让所学知识真正变成你的资产，让学习成为你财富积累的过程！', '2017-04-27', '9787508671581', '中信出版社', '成甲', '2017-04-27 10:08:53', '1');
INSERT INTO `book_details` VALUES ('193', null, null, '人生不必太用力', 'http://open.6api.net/mpic/s29417455.jpg', '1', '38', '为了考所好的大学，挑灯夜战、埋头苦读；\n为了找份好的工作，疲于奔命、心力交瘁；\n好不容易成为社会新鲜人，内心却又充满了彷徨与迷茫；\n如今的我们被赋予了一项看似理所当然的任务：把日子过好。然而事实是，我们每个人都活得辛苦又卑微。可生活居然还在提醒我们：看看那些成功人士，看看你的领导——你还不！够！努！力！于是，焦虑、嫉妒、自卑……各种负能量席卷而来。我们不但没能把日子过好，反而越来越累、越来越苦。\n其实，人生本不必太用力。你就是你，不是只有活成大家都认可的那个样子，才叫作成功；只有走真正适合自己的路，才能到达幸福的彼岸。\n这本书会告诉你，先别急着发力，等找对了方向，你会发现，哪里用得着千军万马过独木桥，只需要一点恰到好处的喜欢和投入，就能顺其自然地拥有无可取代的宝贵人生。', '2017-3', '9787505739857', '中国友谊出版公司', '狗剩爹', '2017-04-27 10:26:11', '1');
INSERT INTO `book_details` VALUES ('194', null, '0', '故事思维', 'http://open.6api.net/mpic/s29331629.jpg', '5', '45', '道理只能赢得辩论，故事可以收服人心\n世界级故事大师安妮特·西蒙斯真正的奠基之作\n《你的团队需要一个会讲故事的人》作者安妮特·西蒙斯真正的奠基之作！\n《你的团队需要一个会讲故事的人》告诉你为什么讲故事胜于讲道理，\n《故事思维》手把手教你讲好一个故事的全套技术。\n...................\n※编辑推荐※\n6大故事模板，9类讲述技巧，10个情景策略，全面升级你的故事思维\n讲故事是一种思维，也是一种技巧。\n在几乎所有场景下，你都能从本书中找到应对技巧和案例参考。\n...................\n※内容简介※\n《故事思维》是将故事思维应用于商界的第一人 ——安妮特 ·西蒙斯的奠基之作，畅销全球十余年。她开创性地提出商界沟通中常用的六大类故事，并详细阐释了在何种情况下、如何讲述这些故事：从获取投资人信任到赢得商业合同，从团队建设到营销活动的开展，彻底讲透了用故事提升个人在商界影响力的方方面面。\n本书认为：说故事永远胜于讲道理。讲好一个故事，可以赋予冰冷的数据和残酷的事实以温情，可以触及最有心机、最强硬、最敌对的人内心深处的柔软所在。说故事并非与生俱来的天赋，是可以学习和逐步提高的思维模式，了解其中关窍所在，你便可以迅速掌控全局。\n本书作者曾培训过微软公司、美国国家航空航天局（NASA）等著名机构，以大量亲身经历的事例为依托，从商业活动的每个细微之处出发，详细讲述实战型的说故事技巧。翻开本书，马上成为三分钟就能讲好一个故事的沟通高手！\n...............\n※推荐语※\n旧经济讲道理，以理性取胜；新经济讲故事，以情感赢得人心！\n创造及诉说故事的能力，是21世纪企业必须拥有的最重要技能。\n——哥本哈根未来研究学院\n在信念不振和乐观消逝的年代，我们需要学习更多关于故事的力量。《故事思维》是一部精彩的著作，能够打动读者的心智、灵魂，留下难以抹灭的记忆，并释放你提升工作效率的能力。每一位希望在个人、组织或全球层面发生正向改变的人，都应该好好研读本书。\n—— 盖尔 ·克里斯托弗 哈佛大学约翰·肯尼迪学院美国政府革新主任\n《故事思维》中充满了能有效提升我们沟通能力的故事，饶有趣味且极具实用性，尤其对于教师和经理人的帮助颇大。\n——埃德加 ·沙因 麻省理工学院斯隆商学院荣誉教授', '2017-3', '9787210075172', '后浪丨江西人民出版社', '[美] 安妮特·西蒙斯（Annette Simmons）', '2017-04-27 10:49:49', '1');

