DROP TABLE IF EXISTS `book_favorites`;
CREATE TABLE `book_favorites` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `uid` bigint(20) DEFAULT NULL COMMENT '用户 id',
  `object_id` bigint(20) DEFAULT NULL COMMENT '收藏内容原来的主键id',
  `addtime` datetime DEFAULT NULL COMMENT '收藏时间',
  `status` tinyint(1) DEFAULT '1' COMMENT '状态1正常，2取消',
  PRIMARY KEY (`id`),
  KEY `sid` (`uid`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=136 DEFAULT CHARSET=utf8 COMMENT='图书收藏表';

