-- ----------------------------
-- Table structure for `book_news`
-- ----------------------------
DROP TABLE IF EXISTS `book_news`;
CREATE TABLE `book_news` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `img` varchar(255) DEFAULT NULL COMMENT '图片',
  `content` longtext COMMENT '内容',
  `addtime` datetime DEFAULT NULL COMMENT '添加时间',
  `type` tinyint(4) DEFAULT '1' COMMENT '0：介绍1:新闻2：公告3：活动4：课程5：讲座 ',
  `status` tinyint(4) DEFAULT '1' COMMENT '状态 ；1 ：正常 2：删除',
  `is_top` tinyint(4) DEFAULT '1' COMMENT '1:置顶;0:不置顶',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=34 DEFAULT CHARSET=utf8 COMMENT='管内信息管理表';

