-- ----------------------------
-- Table structure for `book_order`
-- ----------------------------
DROP TABLE IF EXISTS `book_order`;
CREATE TABLE `book_order` (
  `id` mediumint(8) NOT NULL AUTO_INCREMENT,
  `user_id` mediumint(8) NOT NULL COMMENT '会员id',
  `book_id` mediumint(8) NOT NULL COMMENT '图书id',
  `status` tinyint(1) DEFAULT '0' COMMENT '0未确认 1已确认（未取书） 2已取书 3已还书 4已取消',
  `order_type` tinyint(1) DEFAULT NULL COMMENT '1:借阅2:预约',
  `addtime` datetime DEFAULT NULL COMMENT '申请时间',
  `start_time` datetime DEFAULT NULL COMMENT '取书时间',
  `limit_time` datetime DEFAULT NULL COMMENT '取书期限',
  `back_time` datetime DEFAULT NULL COMMENT '应还时间',
  `back_real_time` datetime DEFAULT NULL COMMENT '实际归还时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=269 DEFAULT CHARSET=utf8 COMMENT='借阅表';

