-- ----------------------------
-- Table structure for `book_users`
-- ----------------------------
DROP TABLE IF EXISTS `book_users`;
CREATE TABLE `book_users` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `user_secret` char(20) DEFAULT NULL COMMENT '密保（找回密码用）',
  `mobile` varchar(50) NOT NULL COMMENT '手机号(处理显示)',
  `user_pass` varchar(64) NOT NULL DEFAULT '###a85e563d9c160067ee1070747f98b42d' COMMENT '登录密码；sp_password加密(默认123456)',
  `corn` bigint(10) DEFAULT '0' COMMENT '会员积分',
  `user_name` varchar(50) DEFAULT NULL COMMENT '真实姓名',
  `sex` smallint(1) DEFAULT '0' COMMENT '性别；0：保密，1：男；2：女',
  `birthday` date DEFAULT NULL COMMENT '生日',
  `user_email` varchar(100) DEFAULT '' COMMENT '登录邮箱',
  `avatar` varchar(100) DEFAULT 'mm1.png' COMMENT '用户头像，相对于upload/avatar目录',
  `addtime` datetime DEFAULT '2000-01-01 00:00:00' COMMENT '注册时间',
  `last_login_time` datetime DEFAULT '2000-01-01 00:00:00' COMMENT '最后登录时间',
  `create_ip` varchar(16) DEFAULT '' COMMENT '注册ip',
  `last_login_ip` varchar(16) DEFAULT '' COMMENT '最后登录ip',
  `user_type` smallint(1) NOT NULL DEFAULT '1' COMMENT '用户类型，1:学生',
  `user_status` smallint(1) DEFAULT '1' COMMENT '用户状态。1:正常2:禁用',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=4040 DEFAULT CHARSET=utf8 COMMENT='会员表';

